package test.kh.java;

public class JavaTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int firstNum = 20;
		int secondNum = 10;
		int result;
		
		result = firstNum + secondNum;
		
		System.out.println(firstNum + "+" + secondNum + "=" + result);
		
		result = firstNum - secondNum;
		
		System.out.println(firstNum + "-" + secondNum + "=" + result);
		
		result = firstNum * secondNum;
		
		System.out.println(firstNum + "*" + secondNum + "=" + result);
		
		result = firstNum / secondNum;
		
		System.out.println(firstNum + "/" + secondNum + "=" + result);
	}

}