package kh.com.java;

public class MathClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(Math.abs(-10));		// 절대값 리턴
		System.out.println(Math.ceil(10.1));	// 주어진 값을 올림
		System.out.println(Math.floor(10.9));	// 주어진 값을 버림
		System.out.println(Math.random());		// 임의의 값 추출
												// 0.0<=random<1.0
		System.out.println(Math.max(1, 2));		// 비교후 큰 값 리턴
	}

}
